public class Bai_tap1 {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
			int a = 16;
	        int b = 4;
	        double c = 3.5;
	        
	        int tong = a + b;
	        double thuong = a / c;
	        int phanDu = a % (int)c;
	       
	        System.out.println("Tổng: " + tong);
	        System.out.println("Thương: " + thuong);
	        System.out.println("Phần dư: " + phanDu);
	}

}
